import React from 'react';
import './component.dropdown.css';

class Dropdown extends React.Component{
    getItems(items){
       return items.map(el => '<option>'+el+'</option>').toString().replace(/,/g,'');
    }
    render(){
        //this.getItems(this.props.options);
        return(

            <div className="input">
                <select>
                    {this.getItems(this.props.options)}
                </select>
            </div>
        );
    }
}

export default Dropdown;
