import React from 'react';
import './component.checkbox.css';

class Checkbox extends React.Component{
    constructor(props) {
        this.state = {
            checked: false
        }
    }

    render(){
        return(
            <div className="Checkbox">
                <label><input type="checkbox" onChange={(e)=>{this.setState({checked: !checked});this.props.isChecked(e)}} ref={checkbox => this.checkbox} />{ this.props.message}</label>
            </div>
        );
    }

}

export default Checkbox;
