import React from 'react';
import './component.dropdown.css';

class Dropdown extends React.Component{
    getItems(items){
        console.log(items);
       return items.map(el => <option>+el+</option>);
    }
    render(){
        let cars = this.getItems(this.props.options);
        let optionItems = cars.map((car) =>
        <option key={car}>{car}</option>
        );
        return(

            <div className="input">
                <select>
                    {this.getItems(this.props.options)}
                </select>
            </div>
        );
    }
}

export default Dropdown;
